        </div>
    {% endfor %}
    <h1>Page de détails de : {{property.title}} </h1>
    <span>Contacter l'agence :</span>
    <div id=\"contactForm\" class=\"mt-4\">
        {{ form_start(form) }}
            <div class=\"row\">
                <div class=\"col-md-4\">{{ form_row(form.firstname) }}</div>
                <div class=\"col-md-4\">{{ form_row(form.lastname) }}</div>
                <div class=\"col-md-4\">{{ form_row(form.phone) }}</div>
                <div class=\"col-md-4\">{{ form_row(form.email) }}</div>
                {{ form_rest(form) }}
                <button class=\"btn btn-secondary\">Envoyer</button>
            </div>
        {{ form_end(form) }}
    </div>
</div>

{% endblock %}
